import React, { Component } from 'react';

class ScoreSport extends Component {
    constructor(props){
        super(props);
        this.onChangeEp = this.onChangeEp.bind(this);
        this.onChangePhysique = this.onChangePhysique.bind(this);
        this.onChangeMoyenneF = this.onChangeMoyenneF.bind(this);
        this.onChangeSciences = this.onChangeSciences.bind(this);
        this.onChangeFrançais = this.onChangeFrançais.bind(this);
        this.onChangeAnglais = this.onChangeAnglais.bind(this);
        this.onChangePhilosophie = this.onChangePhilosophie.bind(this);
        this.onChangeSpp = this.onChangeAnglais.bind(this);
        this.onChangeResult = this.onChangeResult.bind(this);
        this.state={
            spp:0.00,
            ep:0.00,
            physique:0.00,
            moyennef:0.00,
            sciences:0.00,
            français:0.00,
            anglais:0.00,
            philosophie:0.00,
            result:0.00
        }
    }
    onChangeSpp(e) {
        this.setState({
          spp: e.target.value
        })
      }

    onChangeEp(e) {
        this.setState({
          ep: e.target.value
        })
      }

    onChangePhysique(e) {
        this.setState({
          physique: e.target.value
        })
      }

    onChangeMoyenneF(e) {
        this.setState({
          moyennef: e.target.value
        })
      }
      onChangeSciences(e) {
        this.setState({
          sciences: e.target.value
        })
      }
      onChangeFrançais(e) {
        this.setState({
          français: e.target.value
        })
      }
      onChangeAnglais(e) {
        this.setState({
          anglais: e.target.value
        })
      }
      onChangePhilosophie(e) {
        this.setState({
            philosophie: e.target.value
        })
      }
      onChangeResult(e) {
        this.setState({
          result: e.target.value
        })
      }
      cal = () => {
        this.state.result=Number(this.state.moyennef)*4+Number(this.state.physique)*0.5
        +Number(this.state.ep)*0.5+Number(this.state.spp)*1.5
        +Number(this.state.sciences)*1.5+Number(this.state.philosophie)*0.5
        +Number(this.state.anglais)*1+Number(this.state.français)*1
        document.getElementById("result").value = this.state.result;
        return this.state.result;
      }
      onSubmit = (e) => {
        e.preventDefault();

      }

    render(){
        return (
          <div className="wrappersport">
            <div className="form-wrapper800">
            <a href="/" role="button" style={{fontSize:"20px"}}>
            <i className="fa fas-home"></i>
            Home
          </a>
                <center><p style={{fontSize:"40px"}}>Score Baccalauréat Sport </p>
                        <table style={{width:"100%" }}>
                          <tbody style={{ color: '#000000',fontSize: 24 }}>
                            <tr>

                                    <td><label htmlFor="moyennef">Moyenne finale </label></td> 
                                    <td><input 
                                        required
                                        type="text"  
                                        name="mayennef" 
                                        value={ this.state.moyennef}
                                        onChange={this.onChangeMoyenneF}
                                        />
                                    </td> 
                                    <td><label htmlFor="moyennef"> المعدل النهائي للبكالوريا</label></td> 

                            </tr>
                            <tr>
                                <td><label htmlFor="ep">Education physique  </label></td>
                                <td><input 
                                  required   
                                  name="ep" 
                                  value={ this.state.ep}
                                  onChange={this.onChangeEp}
                                  />
                                </td>
                                <td><label htmlFor="ep"> تربية بدنية </label></td>
                            </tr>
                            <tr>

                                    <td><label htmlFor="spp">Sp. sport</label></td> 
                                    <td><input 
                                        required
                                        type="text"  
                                        name="spp" 
                                        value={ this.state.spp}
                                        onChange={this.onChangeSpp}
                                        />
                                    </td> 
                                    <td><label htmlFor="spp"> إختصاص رياضي</label></td> 

                            </tr>
                            <tr>
                                    <td><label htmlFor="physique">Science physique </label></td>
                                    <td><input 
                                        required
                                        type="text"  
                                        name="physique" 
                                        value={ this.state.physique}
                                        onChange={this.onChangePhysique}
                                        />
                                    </td>
                                    <td><label htmlFor="physique"> العلوم الفيزيائية</label></td>

                            </tr>
                            <tr>
                                <td><label htmlFor="sciences">Sciences de la vie et de la Terre </label></td>
                                <td><input 
                                  required   
                                  name="sciences" 
